import React, {useState} from 'react';
import {useHistory} from 'react-router-dom';
import axios from 'axios'
import './styles.css';

var getPosts = async() => {
    var posts;
    await axios.get("/get-news").then(response => posts = response.data).catch(err => console.log(err))
    return posts;

}

var remove = async(id) => {
    await axios.post('/remove-news')
}

var ShowPosts = () => {
    var history = useHistory(); 
    var [posts, setPosts] = useState();
    var [reRender, setReRender] = useState();
    if(!posts){getPosts().then(res => setPosts(res))}
    var prettyPosts = []
    if(posts){

        for(var x = posts.length; x > 0; x--){
            console.log(x)
            prettyPosts.push(
                <div className="post">
                    <img className="post-img" src={posts[x-1].picture} /> 
                    <h3 className="post-thumb-title">{posts[x-1].title}</h3>
                    <span className="author1">من {posts[x-1].author || "مجهول"}</span>
                    <p className="post-thumb-text">{posts[x-1].discraption}</p>
                    <div class="btn-group">
                        <button name={x-1} type="button" class="btn btn-primary" onClick={e => history.push(`/manager/edit-post/${posts[e.target.name]._id}`)}>تعديل <img src="https://img.icons8.com/ios-glyphs/15/ffffff/edit--v1.png"/></button>
                        <button type="button" class="btn btn-secondary">التعليقات<img src="https://img.icons8.com/material-rounded/15/ffffff/comments--v1.png"/></button>
                        <button type="button" onClick={remove} class="btn btn-danger">حذف<img src="https://img.icons8.com/small/16/ffffff/filled-trash.png"/></button>
                    </div>
                    <br />
                    <br />
                    <button name={x-1} className="btn btn-success post-btn" onClick={(e) => {
                        history.push(`/home/${posts[e.target.name]._id}`);
                        }}>انتقل إلى الخبر</button>
                </div>
            )
        }
    }
    console.log(prettyPosts)
    return prettyPosts;
}

var ManagerPosts = () => {
    var history = useHistory();
    var addPost = () => {
        history.push('/manager/add-post')
    }
    return(
        <div>
            <h1>المنشورات</h1>
            <p>حيث يمكنك الإشراف على المنشورات, تعديلها أو حذفها أو إضافة منشور جديد</p>
            <button onClick={addPost} className="btn btn-success">إضافة منشور <img src="https://img.icons8.com/ios/20/ffffff/plus--v2.png"/></button>
            <br />
            <ShowPosts />
        </div>
    );
}

export default ManagerPosts;
