import React from 'react';
import NotAllowed from './notAllowed';
import './styles.css';

var AddPosts = () => {
    if(localStorage.getItem('role') === 'manager' && sessionStorage.getItem('loggedIn')){
        return(
            <div>
                <br />
                <br />
                <h1>
                    إضافة منشور جديد
                </h1>
                <p>املأ الحقول أدناه بمعلومات المنشور ليتم إضافته</p>
                <div className="preview">
                    <span>معاينة</span>
                    <div className="post">
                    <img className="post-img" src="https://th-thumbnailer.cdn-si-edu.com/_fo23UThzt2yYi8vrV0jcafbQ3Q=/fit-in/1600x0/https://tf-cmsv2-smithsonianmag-media.s3.amazonaws.com/filer/25/f0/25f08cff-0355-4001-86ad-28950a866af4/grandteton.jpg" /> 
                    <h3 className="post-thumb-title">العنوان</h3>
                    <span className="author1">من {JSON.parse(localStorage.getItem('managerInfo')).name || "مجهول"}</span>
                    <p className="post-thumb-text">الوصف</p>
                    <button className="btn btn-success post-btn">اقرأ الخبر</button>
                    </div>
                </div>
            </div>  
        );
    }
    else{
        return <NotAllowed />
    }
}

export default AddPosts;
