import React, {useState} from 'react';
import 'react-slideshow-image/dist/styles.css'
import { Slide, Fade } from 'react-slideshow-image';
import axios from 'axios';
import './styles.css';

var Album = () => {
    const slideImages = [
        {
          url: 'data:image/jpeg;base64,',
          caption: 'Slide 1'
        },
        {
          url: 'https://www.cabinetmakerwarehouse.com/wp-content/uploads/969-navy-blue.jpg',
          caption: 'Slide 2'
        },
        {
          url: 'https://pbs.twimg.com/profile_images/1105434753644224512/Sey0zX22_400x400.jpg',
          caption: 'Slide 3'
        },
      ];
      var [selectedImg, SetSelectedImg] = useState(0);
    return (
        <div>
          <div className="slide-container">
            <Slide indicators transitionDuration={500} duration={1200} arrows={false}>
              {slideImages.map((slideImage, index)=> (
                <div className="each-slide thumbnail" key={index}>
                  <button className="thumbnail" onClick={() => SetSelectedImg(index)} data-bs-toggle="modal" data-bs-target="#imagePreview"><img className="albumImage" src={slideImage.url} /></button>
                </div>
              ))} 
            </Slide>
          </div>
          <div className="modal" id="imagePreview">
            <div className="modal-dialog">
              <div className="modal-content">
                <div className="modal-header">
                  <h4 className="modal-title">{slideImages[selectedImg].caption}</h4>
                  <button className="btn-close" data-bs-dismiss="modal"></button>
                </div>
                <div className="modal-body">
                  <img src={slideImages[selectedImg].url} />
                </div>
                <div class="modal-footer">
                  <button type="button" className="btn btn-danger" data-bs-dismiss="modal">Close</button>
                </div>
              </div>
            </div>
          </div>
        </div>
    );
}

export default Album;
