import React, { useState } from 'react';
import NotAllowed from './notAllowed';
import axios from 'axios';
import './styles.css';
import { useHistory } from 'react-router';
import {toast} from 'react-toastify';

var AddTeacher = () => {
    var history = useHistory();
    var [teacherInfo, setTeacherInfo] = useState({
        name: "",
        picture: "",
        passcode: "",
        classes: {grades: "", sections: ""}
    });
    var nameChanged = (e) => {
        e.preventDefault();
        setTeacherInfo({
            name: e.target.name,
            picture: teacherInfo.picture,
            passcode: teacherInfo.passcode,
            classes: teacherInfo.classes
        })
    }
    var passcodeChanged = (e) => {
        e.preventDefault();
        setTeacherInfo({
            name: teacherInfo.name,
            picture: teacherInfo.picture,
            passcode: e.target.passcode,
            classes: teacherInfo.classes
        })
    }
    var gradesChanged = (e) => {
        e.preventDefault();
        setTeacherInfo({
            name: teacherInfo.name,
            picture: teacherInfo.picture,
            passcode: teacherInfo.passcode,
            classes: {grades: e.target.value, sections: teacherInfo.classes.sections}
        })
    }
    var sectionsChanged = (e) => {
        e.preventDefault();
        setTeacherInfo({
            name: teacherInfo.name,
            picture: teacherInfo.picture,
            passcode: teacherInfo.passcode,
            classes: {grades: teacherInfo.classes.grades, sections: e.target.value}
        })
    }
    var pictureChanged = (e) => {
        e.preventDefault();
        let reader = new FileReader();
        let inFile = e.target.files[0];
        reader.onloadend = () => {
            setTeacherInfo({
                name: teacherInfo.name,
                picture: reader.result,
                passcode: teacherInfo.passcode,
                classes: teacherInfo.classes
            })
    }
    reader.readAsDataURL(inFile);
}
    var cancel = () => {
        history.push('/manager')
    }
    var submit = async() => {
        if(teacherInfo.name === "" || teacherInfo.passcode === "" || teacherInfo.classes.grades === "" || teacherInfo.classes.sections === ""){
            toast.error('عليك ملئ جميع الحقول بشكل صحيح', {
                position: "top-right",
                autoClose: 4000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                });
        }else{
            await axios.post('add-teacher', {name: teacherInfo.name, picture: teacherInfo.picture, passcode: teacherInfo.passcode, classes: teacherInfo.classes})
            .then(response => {
                history.push('/manager')
                toast.success('تم إضافة المعلم', {
                    position: "top-right",
                    autoClose: 4000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                    });
            })
        }
    }
    return(
        <div>
            <br />
            <br />
            <h1>إضافة معلم جديد</h1>
            <p>قم بملئ الحقول أدناه بمعلومات المعلم ليتم إضافته</p>
            <label>معاينة:</label>
            <div className="teacher container">
                    <img src={teacherInfo.picture || "https://tse1.mm.bing.net/th?id=OIP.sVrMAmmEljdzKDEba8nttAHaHa&pid=Api&P=0&w=300&h=300"} className='teacher-image' />
                    <br />
                    <div className='row'>
                        <div className="col-6">
                            {teacherInfo.name || "اسم المعلم"}
                        </div>
                        <div className="col-6">
                            <div className="btn-group">
                                <button className="btn btn-primary"><img src="https://img.icons8.com/ios-glyphs/20/ffffff/edit--v1.png"/></button>
                                <button className="btn btn-danger"><img src="https://img.icons8.com/material-rounded/20/ffffff/filled-trash.png"/></button>
                            </div>
                        </div>
                    </div>
                        {teacherInfo.classes.grades || "الفصول"}
                        <br />
                        {teacherInfo.classes.sections || "الشعب"}
            </div>
            <br /><br />
            <form className='section'>
                <div>
                    <label>الإسم:</label>
                    <input onChange={nameChanged} className='form-control' type="text" placeholder='اسم المعلم' />
                </div>
                <div className='form-inline'>
                    <label>الفصول والشعب</label><br />
                    <input onChange={gradesChanged} className='form-control short-input inline' type="text" placeholder='الفصول' />
                    <input onChanged={sectionsChanged} className='form-control short-input inline' type="text" placeholder='الشعب' />
                </div>
                <div>
                    <label>رمز الدخول</label>
                    <input onChanged={passcodeChanged} className='form-control' type="number" placeholder='يتكون من 4 خانات' />
                </div>
                <div>
                    <label>الصورة الشخصية</label>
                    <input onChange={pictureChanged} className='form-control' type="file" />
                    <img src={teacherInfo.picture || "https://tse1.mm.bing.net/th?id=OIP.sVrMAmmEljdzKDEba8nttAHaHa&pid=Api&P=0&w=300&h=300"} />
                </div>
            </form>
            <br />
            <hr />
            <button onClick={submit} type="button" className="btn btn-success form-button">إضافة</button>
            <button onClick={cancel} type="button" className="btn btn-danger form-button">عودة</button>
        </div>
    )
}

export default AddTeacher;
