import  React, { useState } from 'react';
import {useParams} from 'react-router-dom';
import NotAllowed from './notAllowed';
import axios from 'axios';
import './styles.css';

var getPost = async(id) => {
    var post;
    console.log(id);
    await axios.post("/search", {_id: id}).then(response => post = response.data).catch(err => console.log(err))
    console.log(post)
    return post;
}

var EditPost = () => {
    var postId = useParams();
    var [post, setPost] = useState();
    var [title, setTitle] = useState();
    var [discraption, setDiscraption] = useState();
    var [picture, setPicture] = useState();
    var [content, setContent] = useState();
    var [link, setLink] = useState();
    if(!post){getPost(postId.id).then(res => setPost(res))}
    if(localStorage.getItem('managerInfo') && sessionStorage.getItem('loggedIn') || localStorage.getItem('teacherInfo') && sessionStorage.getItem('loggedIn')){
        if(post){
            var titleChanged = (e) => {
                set
            }
            return(
                <div className="addPost">
                    <br />
                    <br />
                    <form>
                        <section className="section">
                            <h4>
                                العنوان
                            </h4>
                            <input onChange={titleChanged} type="text" value={post.title} className="form-control" />
                        </section>
                        <section className="section">
                            <h4>
                                الوصف
                            </h4>
                            <input onChange={discraptionChanged} type="text" value={post.discraption} className="form-control" />
                        </section>
                        <section className="section">
                            <h4>
                                الصورة
                            </h4>
                            <input type="file" onChange={pictureChanged}  filenamw={post.picture} className="form-control" />
                            <img src={post.picture} />
                        </section>
                        <section className="section">
                            <h4>
                                المحتوى
                            </h4>
                            <textarea type="text" onChange={contentChanged}  value={post.content} className="form-control" />
                        </section>
                        <section className="section">
                            <h4>
                                الرابط
                            </h4>
                            <input type="text" onChange={linkChanged} value={post.link || ""} className="form-control" />
                        </section>
                        <section className="section">
                            <h4>
                                عنوان الرابط
                            </h4>
                            <input type="text" onChange={linkTitleChanged} value={post.linkTitle || ""} className="form-control" />
                        </section>
                    </form>
                    <br />
                    <div className="">
                        <button className="btn btn-success form-button">حفظ</button>
                        <button className="btn btn-danger form-button">إلغاء</button>
                    </div>
                </div>
            )
        }
        else{
            return <h1>لم يتم إيجاد المنشور الذي تبحث عنه</h1>
        }
    }
    else{
        return(
            <NotAllowed />
        )
    }
}

export default EditPost;
