import React, {useState} from 'react';
import './styles.css';
import axios from 'axios';

var getTeachers = async() => {
    var teachers;
    await axios.get("/get-teachers").then(response => {teachers = response.data; console.log(response.data)}).catch(err => console.log(err))
    return teachers;

}

var ShowTeachers = () => {
    var [teachers, setTeachers] = useState();
    var prettyTeachers = []
    if(!teachers){getTeachers().then(res => {setTeachers(res); console.log(teachers)})}

    if(teachers && teachers.length === 0 || !teachers){
        return(
            <h1 className='not-found'>لايوجد أي معلمين حتى الآن</h1>
        )
    }
    else if(teachers){
        console.log(teachers)
        for(var x = teachers.length; x >= 0; x--){
            prettyTeachers.push(
                <>
                <div className="teacher row">
                <div className="col-4">
                    {teachers[x+1]}
                </div>
                <div className="col-5">
                    الصف: الأول
                    <br />
                    الشعب: الشعبة 1, الشعبة 2, الشعبة 3, الشعبة 4
                </div>
                <div className="col-3">
                    <div className="btn-group">
                        <button className="btn btn-primary"><img src="https://img.icons8.com/ios-glyphs/20/ffffff/edit--v1.png"/></button>
                        <button className="btn btn-danger"><img src="https://img.icons8.com/material-rounded/20/ffffff/filled-trash.png"/></button>
                    </div>
                </div>
            </div>
            <br />
                </>
            )
        }
        return prettyTeachers;
    }

}

var ManagerTeachers = () => {
    return(
        <div>
            <h1>المعلمين</h1>
            <p>حيث يمكنك الاطلاع على المعلمين, اضافة معلمين او تعديل معلوماتهم</p>
            <button className="btn btn-success">إضافة معلم <img src="https://img.icons8.com/ios/20/ffffff/plus--v2.png"/></button>
            <ShowTeachers />
        </div>
    );
}

export default ManagerTeachers;
