import React, {useState} from 'react';
import axios from 'axios';
import './styles.css';
var News;
var getPosts = async() => {
    var posts;
    await axios.get("/get-news").then(response => posts = response.data).catch(err => console.log(err))
    return posts;

}

var Posts = () => {

    var showPosts = (posts) => {
        var prettyPosts = []
        if(posts){
            for(var x = 0; x < posts.length; x++){
                console.log(x)
                prettyPosts.push(
                    <div className="post">
                        <img className="post-img" src={posts[x].picture} /> 
                        <h3 className="post-thumb-text">{posts[x].title}</h3>
                        <p className="post-thumb-text">{posts[x].discraption}</p>
                        <button className="btn btn-success post-btn" onClick={viewPost(x, posts)}>اقرأ الخبر</button>
                    </div>
                )
            }
        }
        console.log(prettyPosts)
        return prettyPosts;
    }

    var [posts, setPosts] = useState();
    var [selectedPost, setSelectedPost] = useState();
    if(!posts){getPosts().then(res => setPosts(res))}
    console.log(posts)

    var viewPost = async(index, posts) => {
        document.getElementById("main").className = "hide"
        document.getElementById("postView").className = ""
        setSelectedPost(posts[index]);
    }

    return(
        <>
        <div id="main">
            <br />
            <div className="welcome">
                <h3>مرحبًا بك في الموقع الرسمي لثانوية الفيصلية<img src="https://img.icons8.com/material-outlined/30/2C9EA4/sparkling.png"/></h3>
            </div>
            <br />
            <h4>المنشورات</h4>
            <div>
                {showPosts(posts)}
            </div>
        </div>
        <div id="postView" className="hide">
            <h1>{selectedPost? selectedPost.title:""}</h1>
            <img src={selectedPost? selectedPost.picture:""} />
            <p>{selectedPost? selectedPost.content:""}</p>
        </div>
        </>
    );
}

export default Posts;
