import React, {useState} from 'react';
import './styles.css';
import axios from 'axios';
import {toast} from 'react-toastify';

var getTeachers = async() => {
    var teachers;
    await axios.get("/get-teachers").then(response => {teachers = response.data; console.log(response.data)}).catch(err => console.log(err))
    return teachers;

}

var ShowTeachers = () => {
    var [teachers, setTeachers] = useState();
    var prettyTeachers = []
    if(!teachers){getTeachers().then(res => {setTeachers(res); console.log(teachers)})}

    var remove = async e => {
        e.preventDefault();
        await axios.post('/remove-teacher', {_id: teachers[e.target.name]._id}).then(res => {
            window.location.reload();
            toast.success('تم إزالة المعلم', {
                position: "top-right",
                autoClose: 4000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                });
        })
    }

    if(teachers && teachers.length === 0 || !teachers){
        return(
            <h1 className='not-found'>لايوجد أي معلمين حتى الآن</h1>
        )
    }
    else if(teachers){
        console.log(teachers)
        for(var x = teachers.length; x > 0; x--){
            prettyTeachers.push(
                <>
                <div className="teacher container">
                    <img src={teachers[x-1].picture} className='teacher-image' />
                    <br />
                    <div className='row'>
                        <div className="col-6">
                            {teachers[x-1].name}
                        </div>
                        <div className="col-6">
                            <div className="btn-group">
                                <button name={x-1} className="btn btn-primary" ><img src="https://img.icons8.com/ios-glyphs/20/ffffff/edit--v1.png"/></button>
                                <button name={x-1} className="btn btn-danger" onClick={remove}><img src="https://img.icons8.com/material-rounded/20/ffffff/filled-trash.png"/></button>
                            </div>
                        </div>
                    </div>
                        {teachers[x-1].classes[0].grade}
                        <br />
                        {teachers[x-1].classes[0].section}
            </div>
            <br />
                </>
            )
        }
        return prettyTeachers;
    }

}

var ManagerTeachers = () => {
    return(
        <div>
            <h1>المعلمين</h1>
            <p>حيث يمكنك الاطلاع على المعلمين, اضافة معلمين او تعديل معلوماتهم</p>
            <button className="btn btn-success">إضافة معلم <img src="https://img.icons8.com/ios/20/ffffff/plus--v2.png"/></button>
            <ShowTeachers />
        </div>
    );
}

export default ManagerTeachers;
