import React, { useState } from 'react';
import NotAllowed from './notAllowed';
import './styles.css';

var AddPosts = () => {
    var [postTitle, setPostTitle] = useState("العنوان");
    var [postDiscraption, setPostDiscraption] = useState("وصف المنشور");
    var [postImage, setPostImage] = useState("data:image/png;base64,");
    var [postTitle, setPostTitle] = useState("محتوى المنشور");
    var [postLink, setPostLink] = useState("");
    var [postLinkTitle, setPostLinkTitle] = useState("عنوان الرابط");
    if(localStorage.getItem('role') === 'manager' && sessionStorage.getItem('loggedIn')){
        return(
            <div className="addPost">
                <br />
                <br />
                <h1>
                    إضافة منشور جديد
                </h1>
                <p>املأ الحقول أدناه بمعلومات المنشور ليتم إضافته</p>
                <div className="preview">
                    <label>معاينة</label><br />
                    <div className="post">
                    <img className="post-img" src="data:image/png;base64," /> 
                    <h3 className="post-thumb-title">{postTitle}</h3>
                    <span className="author1">من {JSON.parse(localStorage.getItem('managerInfo')).name || "مجهول"}</span>
                    <p className="post-thumb-text">{postDiscraption}</p>
                    <button className="btn btn-success post-btn">اقرأ الخبر</button>
                    </div>
                </div>
                <hr />
                <form>
                    <span>*عنوان المنشور</span>
                    <input className="form-control" type="text" placeholder="أدخل عنوان المنشور هنا" />
                    <span>*وصف المنشور</span>
                    <input className="form-control" type="text" placeholder="أدخل وصف المنشور هنا" />
                    <span>*صورة المنشور</span>
                    <input className="form-control" type="file" />
                    <span>*محتوى المنشور</span>
                    <textarea className="form-control" type="text" placeholder="أدخل محتوى المنشور هنا" />
                    <span>الرابط</span>
                    <input className="form-control" type="text" placeholder="في حال تواجد صفحة أو ملف متعلق بهذا المنشور, ادخل الرابط هنا" />
                    <span>عنوان الرابط</span>
                    <input className="form-control" type="text" placeholder="أدخل عنوان رابط المنشور هنا" />
                </form>
            </div>  
        );
    }
    else{
        return <NotAllowed />
    }
}

export default AddPosts;
