import  React, { useState } from 'react';
import {useParams} from 'react-router-dom';
import NotAllowed from './notAllowed';
import axios from 'axios';
import './styles.css';

var getPost = async(id) => {
    var post;
    console.log(id);
    await axios.post("/search", {_id: id}).then(response => post = response.data).catch(err => console.log(err))
    console.log(post)
    return post;
}

var EditPost = () => {
    var postId = useParams();
    var [post, setPost] = useState();

    var [postInfo, setPostInfo] = useState();

    if(!post && !postInfo){getPost(postId.id).then(res => {
        setPost(res);
        setPostInfo(res)
        console.log(postInfo)
    })
}

    var titleChanged = (e) => {
        e.preventDefault();
        setPostInfo({
            title: e.target.value, 
            discraption: postInfo.discraption, 
            picture: postInfo.picture, 
            content: postInfo.content, 
            link: postInfo.link, 
            linkTitle: postInfo.linkTitle})
    }

    var discraptionChanged = (e) => {
        e.preventDefault();
        setPostInfo({
            title: postInfo.title, 
            discraption: e.target.value, 
            picture: postInfo.picture, 
            content: postInfo.content, 
            link: postInfo.link, 
            linkTitle: postInfo.linkTitle})
    }
    var pictureChanged = (e) => {
        e.preventDefault();
        setPostInfo({
            title: postInfo.title, 
            discraption: postInfo.discraption, 
            picture: e.target.value, 
            content: postInfo.content, 
            link: postInfo.link, 
            linkTitle: postInfo.linkTitle})
    }
    var contentChanged = (e) => {
        e.preventDefault();
        setPostInfo({
            title: postInfo.title, 
            discraption: postInfo.discraption, 
            picture: postInfo.picture, 
            content: e.target.value, 
            link: postInfo.link, 
            linkTitle: postInfo.linkTitle})
    }
    var linkChanged = (e) => {
        e.preventDefault();
        setPostInfo({
            title: postInfo.title, 
            discraption: postInfo.discraption, 
            picture: postInfo.picture, 
            content: postInfo.content, 
            link: postInfo.link, 
            linkTitle: postInfo.linkTitle})
    }
    var linkTitleChanged = (e) => {
        e.preventDefault();
        setPostInfo({
            title: e.target.value, 
            discraption: postInfo.discraption, 
            picture: postInfo.picture, 
            content: postInfo.content, 
            link: postInfo.link, 
            linkTitle: postInfo.linkTitle})
    }
    if(localStorage.getItem('managerInfo') && sessionStorage.getItem('loggedIn') || localStorage.getItem('teacherInfo') && sessionStorage.getItem('loggedIn')){
        if(post && postInfo){
            return(
                <div className="addPost">
                    <br />
                    <br />
                    <form>
                        <section className="section">
                            <h4>
                                العنوان
                            </h4>
                            <input onChange={titleChanged} type="text" value={postInfo.title} className="form-control" />
                        </section>
                        <section className="section">
                            <h4>
                                الوصف
                            </h4>
                            <input onChange={discraptionChanged} type="text" value={postInfo.discraption} className="form-control" />
                        </section>
                        <section className="section">
                            <h4>
                                الصورة
                            </h4>
                            <input type="file" onChange={pictureChanged}  filenamw={postInfo.picture} className="form-control" />
                            <img src={post.picture} />
                        </section>
                        <section className="section">
                            <h4>
                                المحتوى
                            </h4>
                            <textarea type="text" onChange={contentChanged}  value={postInfo.content} className="form-control" />
                        </section>
                        <section className="section">
                            <h4>
                                الرابط
                            </h4>
                            <input type="text" onChange={linkChanged} value={postInfo.link || ""} className="form-control" />
                        </section>
                        <section className="section">
                            <h4>
                                عنوان الرابط
                            </h4>
                            <input type="text" onChange={linkTitleChanged} value={postInfo.linkTitle || ""} className="form-control" />
                        </section>
                    </form>
                    <br />
                    <div className="">
                        <button className="btn btn-success form-button">حفظ</button>
                        <button className="btn btn-danger form-button">إلغاء</button>
                    </div>
                </div>
            )
        }
        else{
            return <h1>لم يتم إيجاد المنشور الذي تبحث عنه</h1>
        }
    }
    else{
        return(
            <NotAllowed />
        )
    }
}

export default EditPost;
