import React, {useState} from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

var getPost = async(id) => {
    var post;
    console.log(id);
    await axios.post("/search", {_id: id}).then(response => post = response.data).catch(err => console.log(err))
    console.log(post)
    return post;
}

var Comments = () => {
    var postId = useParams();
    var [post, setPost] = useState();
    var [title, setTitle] = useState();
    var [comments, setComments] = useState();
    if(!post){getPost(postId).then(res => setPost(res))}
    if(post && !comments && !title){setComments(post.comments); setTitle(post.title)}
    return(
        <div>
            <br />
            <br />
            <h1>{title}</h1>
        </div>
    );
}

export default Comments;
