import  React, { useState } from 'react';
import {useParams} from 'react-router-dom';
import NotAllowed from './notAllowed';
import axios from 'axios';
import './styles.css';

var getPost = async(id) => {
    var post;
    console.log(id);
    await axios.post("/search", {_id: id}).then(response => post = response.data).catch(err => console.log(err))
    console.log(post)
    return post;
}

var EditPost = () => {
    var postId = useParams();
    var [post, setPost] = useState();
    if(!post){getPost(postId.id).then(res => setPost(res))}
    if(localStorage.getItem('managerInfo') && sessionStorage.getItem('loggedIn') || localStorage.getItem('teacherInfo') && sessionStorage.getItem('loggedIn')){
        return(
            <div className="addPost">
                <br />
                <br />
                <form>
                    <section>
                        <h4>
                            العنوان
                        </h4>
                        <input type="text" value={post.title} className="form-control" />
                    </section>
                    <section>
                        <h4>
                            الوصف
                        </h4>
                        <input type="text" value={post.discraption} className="form-control" />
                    </section>
                    <section>
                        <h4>
                            الصورة
                        </h4>
                        <input type="file" value={post.picture} className="form-control" />
                        <img src={post.picture} />
                    </section>
                    <section>
                        <h4>
                            المحتوى
                        </h4>
                        <input type="text" value={post.content} className="form-control" />
                    </section>
                    <section>
                        <h4>
                            الرابط
                        </h4>
                        <input type="text" value={post.link || ""} className="form-control" />
                    </section>
                    <section>
                        <h4>
                            عنوان الرابط
                        </h4>
                        <input type="text" value={post.linkTitle} className="form-control" />
                    </section>
                </form>
            </div>
        )
    }
    else{
        return(
            <NotAllowed />
        )
    }
}

export default EditPost;
