import React, { useState } from 'react';
import NotAllowed from './notAllowed';
import axios from 'axios';
import './styles.css';
import { useHistory } from 'react-router';
import {toast} from 'react-toastify';

var AddTeacher = () => {
    return(
        <div>
            <br />
            <br />
            <h1>إضافة معلم جديد</h1>
            <p>قم بملئ الحقول أدناه بمعلومات المعلم ليتم إضافته</p>
            <label>معاينة:</label>
            <div className="teacher container">
                    <img src="https://tse1.mm.bing.net/th?id=OIP.sVrMAmmEljdzKDEba8nttAHaHa&pid=Api&P=0&w=300&h=300" className='teacher-image' />
                    <br />
                    <div className='row'>
                        <div className="col-6">
                            اسم المعلم
                        </div>
                        <div className="col-6">
                            <div className="btn-group">
                                <button name={x-1} className="btn btn-primary" onClick={e => history.push(`/manager/edit-teacher/${teachers[e.target.name]._id}`)} ><img src="https://img.icons8.com/ios-glyphs/20/ffffff/edit--v1.png"/></button>
                                <button name={x-1} className="btn btn-danger" onClick={remove}><img src="https://img.icons8.com/material-rounded/20/ffffff/filled-trash.png"/></button>
                            </div>
                        </div>
                    </div>
                        {teachers[x-1].classes[0].grade}
                        <br />
                        {teachers[x-1].classes[0].section}
            </div>
        </div>
    )
}

export default AddTeacher;
