import React, {useState} from 'react';
import ManagerPosts from './managerPosts';
import NotAllowed from './notAllowed';
import './styles.css';

var Manager = () => {
    var [managerInfo, setManagerInfo] = useState(JSON.parse(localStorage.getItem('managerInfo')));
    var [view, setView] = useState(<ManagerPosts />);
    if(!sessionStorage.getItem("loggedIn")){
        return <NotAllowed />
    }
    else{
        var postsView = (e) => {
            e.preventDefault();
            setView(<ManagerPosts />);
            document.getElementById('posts').className = "col-4 btn btn-primary posts-btn"
            document.getElementById('teachers').className = "col-4 btn btn-outline-secondary"
            document.getElementById('album').className = "col-4 btn btn-outline-success"
        }
        var teachersView = (e) => {
            e.preventDefault();
            setView(<ManagerPosts />);
            document.getElementById('posts').className = "col-4 btn btn-outline-primary"
            document.getElementById('teachers').className = "col-4 btn btn-secondary teachers-btn"
            document.getElementById('album').className = "col-4 btn btn-outline-primary album-btn"
        }
        var albumView = (e) => {
            e.preventDefault();
            setView(<ManagerPosts />);
            document.getElementById('posts').className = "col-4 btn btn-primary posts-btn"
            document.getElementById('teachers').className = "col-4 btn btn-primary teachers-btn"
            document.getElementById('album').className = "col-4 btn btn-primary album-btn"
        }
        console.log(managerInfo)
        return(
            <div>
                <br />
                <br />
                <h1> مرحبا {managerInfo.name} ! </h1>
                <div className="sticky-top">
                    <p>هذه هي صفحتك الخاصة لإدارة الموقع, قم باختيار ماتريد القيام به من الخيارات أدناه</p>
                    <div className="manager-nav">
                        <div className="row">
                            <button id="posts" onClick={postsView} className="col-4 btn btn-primary posts-btn selected">
                                المنشورات
                            </button>
                            <button id="teachers" onClick={teachersView} className="col-4 btn teachers-btn btn-primary">
                                المعلمين
                            </button>
                            <button id="album" onClick={albumView} className="col-4 btn album-btn btn-primary">
                                الألبوم
                            </button>
                            <div>
                                {view}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Manager;
