import React, {useState} from "react";
import axios from 'axios';
import {useParams, useHistory} from 'react-router-dom';
import './styles.css';

var getTeacher = async(id) => {
    var teacher;
    console.log(id);
    await axios.post("/search-teachers", {_id: id}).then(response => teacher = response.data).catch(err => console.log(err))
    console.log(teacher)
    return teacher;
}

var EditTeacher = () => {
    var history = useHistory();
    var postId = useParams();
    var [teacher, setTeacher] = useState();
    var [teacherInfo, setTeacherInfo] = useState();
    console.log(postId)
    if(!teacher && !teacherInfo){getTeacher(postId.id).then(res => {
        setTeacher(res);
        setTeacherInfo(res)
        console.log(teacher)

    })
    return (
        <div>لم يتم ايجاد المعلومات التي تبحث عنها</div>
    )
}

    else{
        return(
            <div>
                <br />
                <br />
                <h4>تعديل بيانات {teacher.name}</h4>
                <br />
                <form>
                    <label>الاسم</label>
                    <input type="text" className="form-control" placeholder={teacher.name}/>
                    <label>رمز الدخول</label>
                    <input type="text" className="form-control" placeholder={teacher.passcode}/>
                </form>
            </div>
        )   
    }
}

export default EditTeacher;
