import React, {useState} from 'react';
import axios from 'axios';
import './styles.css';
import { useParams } from 'react-router';

var getPost = async(id) => {
    var post;
    console.log(id);
    await axios.post("/search", {_id: id}).then(response => post = response.data).catch(err => console.log(err))
    console.log(post)
    return post;
}
var showComments = (post) => {
    var rawComments = post.comments;
    var comments = [];
    if(rawComments.length === 0){
        return (<p className="post-view-content">لا يوجد تعليق حتى الآن</p>);
    }
    else{
        for(var x = rawComments.length; x > 0; x--){
            comments.push(<p className="comment">{rawcomments[x - 1]}</p>)
        }
    }
    return comments
}

var PostView = () => {
    var postId = useParams();
    console.log(postId)
    var [post, setPost] = useState();
    var [comments, setComments] = useState();
    if(!post){getPost(postId.id).then(res => setPost(res))}
    if(post && !comments){showComments(post).then(res => setComments(res))}
    console.log(post)
    if(post){
        return(
            <div>
                <br />
                <br />
                <h1 className="post-view-title">{post.title}</h1>
                <img className="post-view-image mx-auto d-block" src={post.picture} />
                <p className="post-view-content">{post.content}</p>
                <hr />
                <button className="btn btn-outline-danger">إعجاب <i className="fas fa-heart"></i></button>
                <br />
                <br />
                <form>
                    <input className="form-control" placeholder="اترك تعليق!" />
                    <button className="btn btn-primary">إرسال</button>
                </form>
                <br />

                <h3>التعليقات:</h3>

                
            </div>
        );
    }
    else{
        return(
            <div>
                <br />
                <br />
                <h1>لم يتم إيجاد المنشور الذي تبحث عنه !</h1>
            </div>
        )
    }
}

export default PostView;
