import React from 'react';
import { useHistory } from 'react-router';
import './styles.css';
 
var LogIn = () => {
    var history = useHistory();
    if(localStorage.getItem("role")){
        if(localStorage.getItem("role") === "manager"){
            history.push('/manager')
        }
        else if(localStorage.getItem("role" === "teacher")){
            history.push(`/teacher/${JSON.parse(localStorage.getItem("teacherInfo")).passcode}`)
        }
    }
    return(
        <div className="big">
            <br /><br /><br />
            <div className="login-form">
                <h1 className="login-form-header">أدخل رمز المرور الخاص بك</h1>
                <br />
                <input type="number" onChange={passcodeChanged} className="form-control" />
                <br />
                <button className="btn text-light button"><span>دخول</span></button>
            </div>
        </div>
    )
}

export default LogIn;
