import React, {useState} from 'react';
import axios from 'axios';
import './styles.css';
var News, posts;
var getPosts = () => {
    axios.get('/get-news').then(response => {
        posts = response.data;
    }).catch(err => {
        console.log(err)
    })
}
var showPosts = (rawPosts) => {
    var prettyPosts = []
    for(var x = 0; x <= rawPosts.length; x++){
        prettyPosts.push(
            <div className="post">
                <img className="post-img" src={rawPosts[x].picture} /> 
                <h3 className="post-thumb-text">{rawPosts[x].title}</h3>
                <p className="post-thumb-text">{rawPosts[x].discraption}</p>
                <button className="btn btn-success post-btn">اقرأ الخبر</button>
            </div>
        )
    }
    console.log(prettyPosts)
    News = prettyPosts;
}
var Posts = () => {
    getPosts();
    if(posts){
        showPosts();
        console.log(News);
        return(
            <div>
                <br />
                <div className="welcome">
                    <h3>مرحبًا بك في الموقع الرسمي لثانوية الفيصلية<img src="https://img.icons8.com/material-outlined/30/2C9EA4/sparkling.png"/></h3>
                </div>
                <br />
                <h4>المنشورات</h4>
                <div>
                    <News />
                </div>
            </div>
        );
    }
    return(
        <div>
            <br />
            <div className="welcome">
                <h3>مرحبًا بك في الموقع الرسمي لثانوية الفيصلية<img src="https://img.icons8.com/material-outlined/30/2C9EA4/sparkling.png"/></h3>
            </div>
            <br />
            <h4>المنشورات</h4>
            <div>
                <h1>fnf</h1>
            </div>
        </div>
    );
}

export default Posts;
