import React, {useState} from 'react';
import axios from 'axios';
import './styles.css';
var News, posts;
var getPosts = async () => {
    try{
        posts = await axios.get('/get-news')
    }catch(err){
        return null;
    }
}

var showPosts = () => {
    var prettyPosts = []
    if(posts){
        for(var x = 0; x <= posts.data.length; x++){
            prettyPosts.push(
                <div className="post">
                    <img className="post-img" src={posts.data[x].picture} /> 
                    <h3 className="post-thumb-text">{posts.data[x].title}</h3>
                    <p className="post-thumb-text">{posts.data[x].discraption}</p>
                    <button className="btn btn-success post-btn">اقرأ الخبر</button>
                </div>
            )
        }
    }
    console.log(prettyPosts)
    News = prettyPosts;
}
var Posts = () => {
    console.log(posts)
    getPosts();
    showPosts();
    console.log(News);
    return(
        <div>
            <br />
            <div className="welcome">
                <h3>مرحبًا بك في الموقع الرسمي لثانوية الفيصلية<img src="https://img.icons8.com/material-outlined/30/2C9EA4/sparkling.png"/></h3>
            </div>
            <br />
            <h4>المنشورات</h4>
            <div>
                {News}
            </div>
        </div>
    );
}

export default Posts;
